@Component({
  selector: 'app-button',
  templateUrl: './button.component.html',
  styleUrls: ['./button.component.css']
})
export class ButtonComponent implements OnInit{

@Input() text: string;
@Input() color: string;


constructor(){}

ngOnInit(): void {

}

onClick(){
  console.log('This is a debug message from a button')
}

}
