@Component({
  selector: 'app-button',
  templateUrl: './button.component.html',
  styleUrls: ['./button.component.css']
})
export class ButtonComponent implements OnInit{

@Input() text: string;
@Input() color: string;


ngOnInit(): void {

}

}
<button [ngClass]="{'background-color': color}" class="btn"> {{text}} </button>
